import {React, useState, useEffect} from 'react';
import {Input, Carousel , Table, Tag, InputNumber, Popconfirm, Form, Typography, Button, Image } from 'antd';
import { database } from '../../Firebase/firebase';//database ref o  firebase.js
import { ref, set, push, onValue } from "firebase/database";//database

const { Search } = Input;


//#########################
const EditableCell = ({
  editing,
  dataIndex,
  title,
  inputType,
  record,
  index,
  children,
  ...restProps
}) => {
  const inputNode = inputType === 'number' ? <InputNumber /> : <Input />;
  return (
    <td {...restProps}>
      {editing ? (
        <Form.Item
          name={dataIndex}
          style={{
            margin: 0,
          }}
          rules={[
            {
              required: true,
              message: `Please Input ${title}!`,
            },
          ]}
        >
          {inputNode}
        </Form.Item>
      ) : (
        children
      )}
    </td>
  );
};

const EditableTable = (props) => {
  const [form] = Form.useForm();
  const [data, setData] = useState([]);
  const [editingKey, setEditingKey] = useState('');

  const isEditing = (record) => record.key === editingKey;

  
  useEffect(() =>{
    const inventarioRef = ref(database, "Inventario");
    onValue(inventarioRef, (snapshot) => {
      const data = snapshot.val();
      const tempEntryList = [];
      for(let i in data){
        let entryObject = data[i];
        entryObject["key"] = i;
        tempEntryList.push(entryObject);
        console.log(entryObject);
      }
      setData(tempEntryList);
    })
  }, []);

  const edit = (record) => {
    form.setFieldsValue({
      categoria: '',
      articulo: '',
      cantidad: '',
      fotos: '',
      ...record,
    });
    setEditingKey(record.key);
  };

  const cancel = () => {
    setEditingKey('');
  };

  const save = async (key) => {
    try {
      const row = await form.validateFields();
      const newData = [...data];
      const index = newData.findIndex((item) => key === item.key);

      if (index > -1) {
        const inventarioRef = ref(database, "Inventario/"+key);
        set(inventarioRef,row);

        const item = newData[index];
        newData.splice(index, 1, { ...item, ...row });
        setData(newData);
        setEditingKey('');
      } else {
        newData.push(row);
        setData(newData);
        setEditingKey('');
      }
    } catch (errInfo) {
      console.log('Validate Failed:', errInfo);
    }
  };
  const columns = [
    {
      title: 'Categoria',
      dataIndex: 'categoria',
      width: "30%",
      editable: true,
    },
    {
      title: 'Articulo',
      dataIndex: 'articulo',
      width: "40%",
      editable: true,
      render: (text, record) => (
        <div>
            <a href={"/articulos?name="+text}>{text}</a>
        </div>
      ),
    },
    {
      title: 'Cant.',
      dataIndex: 'cantidad',
      width: "10%",
      editable: true,
    },
    {
      title: 'Operacion',
      dataIndex: 'operation',
      width: "20%",
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <span>
            <Typography.Link
              onClick={() => save(record.key)}
              style={{
                marginRight: 8,
              }}
            >
              Guardar
            </Typography.Link>
            <Popconfirm title="Quieres cancelar la ediciÃ³n?" onConfirm={cancel}>
              <a>Cancelar</a>
            </Popconfirm>
          </span>
        ) : (
          <div>
            <Tag color='geekblue' disabled={editingKey !== ''} onClick={() => edit(record)}>
              EDITAR
            </Tag>
            <Tag color='geekblue' onClick={verFotos} value={record.articulo}>
              VER
            </Tag>
          </div>
          
        );
      },
    },
  ];
  const mergedColumns = columns.map((col) => {
    if (!col.editable) {
      return col;
    }

    return {
      ...col,
      onCell: (record) => ({
        record,
        inputType: col.dataIndex === 'cantidad' ? 'number' : 'text',
        dataIndex: col.dataIndex,
        title: col.title,
        editing: isEditing(record),
      }),
    };
  });
  function verFotos(e){
    let imgCount = 1;
    let imgCountToStr = ("00" + imgCount).slice(-2);
    let url = "./" + Object.values(e.target)[1]['value']+imgCountToStr+".jpg";
    let allImages = [];
    allImages = allImages.concat(url);
    imgCount += 1;
    imgCountToStr = ("00" + imgCount).slice(-2);
    url = "./" + Object.values(e.target)[1]['value']+imgCountToStr+".jpg";
    allImages = allImages.concat(url);
    imgCount += 1;
    imgCountToStr = ("00" + imgCount).slice(-2);
    url = "./" + Object.values(e.target)[1]['value']+imgCountToStr+".jpg";
    allImages = allImages.concat(url);
    console.log(allImages);
    props.imageUpdate(allImages);
  }
  return (
    <Form form={form} component={false}>
      <Table
        components={{
          body: {
            cell: EditableCell,
          },
        }}
        bordered
        dataSource={data}
        columns={mergedColumns}
        rowClassName="editable-row"
        pagination={{
          onChange: cancel,
        }}
      />
    </Form>
  );
};

//###########################
function Principal() {  
  const [images, setImages] =useState([]);
  const contentStyle = {
    maxHeight: '50vh',
    maxWidth: '100%',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
  }; 

  function nuevoArticulo(){
    const inventarioRef = ref(database, "Inventario");
    const entry ={
      categoria: 'none',
      articulo: 'none',
      cantidad: 0,
    };
    push(inventarioRef,entry);
  }

  return (
    <div>
      <div className ="contentHeader" style={{padding: "5px"}}>
        <Search placeholder="Buscar"  allowClear style={{ width: 200}} />{/*onSearch={onSearch}*/}
        <Button type="primary" style={{float: "right"}} onClick={nuevoArticulo}>Nuevo Articulo</Button>
      </div>
      <EditableTable imageUpdate = {setImages}/>
      <Carousel >
        {images.map( (image, c) => {
          return(
            <div key={c}>
              <Image 
              style={contentStyle} 
              src={image}
              fallback="data:image/png;base64,"
              />
            </div>
          );
        })}
      </Carousel>
      
    </div>
  );
}

export default Principal;